mapper.select:
select
ID,MON,SN,ARREARAGE_NO,METER_ID,DEDUCTION_BALANCE,ARREARS,FACT_MONEY,FACT_PUNISH,FACT_PRE,PAY_DATE,IN_DATE,BALANCE_FLAG,F_CHARGE_MODE,SETTLEMENT_ID,PAID_FLAG,RELA_USER_NO,OPERATOR,REMARK,STATUS,FLOW_NO,APP_NO,JZ_FLAG,FACT_TOTAL,METER_MONEY_ID,CHARGE_MODE,
BUSINESS_PLACE_CODE from CHARGE_INFO


        and ID = #{id}


        and MON = #{mon}


        and SN = #{sn}


        and ARREARAGE_NO = #{arrearageNo}



            and METER_ID in

                #{item}




                and METER_ID = #{meterId}







        and MON in

            #{item}



        and DEDUCTION_BALANCE = #{deductionBalance}


        and ARREARS = #{arrears}


        and FACT_MONEY = #{factMoney}


        and FACT_PUNISH = #{factPunish}


        and FACT_PRE = #{factPre}


        and PAY_DATE <= #{payDate}


        and IN_DATE = #{inDate}


        and BALANCE_FLAG = #{balanceFlag}


        and F_CHARGE_MODE = #{fChargeMode}


        and BUSINESS_PLACE_CODE = #{businessPlaceCode}


        and SETTLEMENT_ID = #{settlementId}


        and PAID_FLAG = #{paidFlag}


        and RELA_USER_NO = #{relaUserNo}


        and OPERATOR = #{operator}


        and REMARK = #{remark}


        and STATUS = #{status}


        and FLOW_NO = #{flowNo}


        and APP_NO = #{appNo}


        and JZ_FLAG = #{jzFlag}


        and FACT_TOTAL = #{factTotal}


        and METER_MONEY_ID = #{meterMoneyId}


        and CHARGE_MODE = #{chargeMode}

select a.RECEIVABLE, c.id, n.SETTLEMENT_NAME, c.YS_TYPE_CODE,c.PAY_DATE, count(DISTINCT c.METER_ID) as meterNum, c.METER_ID, c.OPERATOR, n.SETTLEMENT_ID, n.SETTLEMENT_NO, n.LAST_BALANCE, N.THIS_BALANCE, IFNULL(SUM(c.FACT_TOTAL),0)as factTotal,IFNULL(SUM(m.TOTAL_POWER),0)as totalPower,IFNULL(SUM(c.FACT_MONEY),0)as factMoney,IFNULL(SUM(m.VOLUME_CHARGE),0)as volumeCharge,IFNULL(SUM(m.POWER_RATE_MONEY),0)as powerRateMoney,IFNULL(SUM(m.BASIC_MONEY),0)as basicMoney,IFNULL(SUM(m.ADD_MONEY2),0)as addMoney2,IFNULL(SUM(m.ADD_MONEY3),0)as addMoney3, IFNULL(SUM(m.ADD_MONEY4),0)as addMoney4,IFNULL(SUM(m.ADD_MONEY5),0)as addMoney5,IFNULL(SUM(m.ADD_MONEY6),0)as addMoney6,IFNULL(SUM(m.ADD_MONEY7),0)as addMoney7,IFNULL(SUM(m.ADD_MONEY8),0)as addMoney8,IFNULL(SUM(m.ADD_MONEY9),0)as addMoney9,IFNULL(SUM(c.FACT_PUNISH),0)as factPunish,IFNULL(SUM(c.FACT_PRE),0)as factPre,c.mon, c.F_CHARGE_MODE,IFNULL(n.NOTE_PRINT_NO,'')NOTE_PRINT_NO,n.PRICE_TYPE_ID,c.ARREARS, w.start_num,w.end_num,w.factor_num from CHARGE_INFO c left join ARREARAGE_INFO a ON a.ARREARAGE_NO=c.ARREARAGE_NO left join NOTE_INFO n ON c.id=n.CHARGE_INFO_ID left join METER_MONEY_INFO m on c.METER_MONEY_ID=m.ID left join WRITE_FILES_INFO w on w.METER_ID=a.METER_ID AND w.MON=a.MON AND w.SN=a.SN AND w.time_seg=0 where c.OPERATOR!=9999 and c.f_CHARGE_MODE not in (4,5,6)
and n.SETTLEMENT_NO =#{settParam}
and c.OPERATOR =#{userParam}
and c.BUSINESS_PLACE_CODE =#{deptParam}
and a.WRITE_SECT_ID =#{writeSectIdParam}
and c.mon>=#{startMon}
and c.mon<=#{endMon}
and c.f_charge_mode=#{fChargeMode}
and c.pay_date >= #{startDate}
and c.pay_date <= #{endDate}
GROUP BY c.SETTLEMENT_ID,c.mon,c.PAY_DATE

select a.RECEIVABLE, c.id, n.SETTLEMENT_NAME, c.YS_TYPE_CODE,c.PAY_DATE, count(DISTINCT c.METER_ID) as meterNum, c.METER_ID, c.OPERATOR, n.SETTLEMENT_ID, n.SETTLEMENT_NO, n.LAST_BALANCE, N.THIS_BALANCE, c.FACT_TOTAL,m.TOTAL_POWER,c.FACT_MONEY,m.VOLUME_CHARGE,m.POWER_RATE_MONEY,m.BASIC_MONEY,m.ADD_MONEY2,m.ADD_MONEY3, m.ADD_MONEY4,m.ADD_MONEY5,m.ADD_MONEY6,m.ADD_MONEY7,m.ADD_MONEY8,m.ADD_MONEY9,M.ADD_POWER,M.ACTIVE_TRANSFORMER_LOSS_POWER, M.TOTAL_POWER,M.AMOUNT,M.LADDER1_POWER,M.LADDER2_POWER,M.LADDER3_POWER,M.LADDER4_POWER,M.LADDER1_MONEY,M.LADDER2_MONEY,M.LADDER3_MONEY,M.LADDER4_MONEY, M.LADDER1_LIMIT,M.LADDER2_LIMIT,M.LADDER3_LIMIT,M.LADDER4_LIMIT c.FACT_PUNISH,c.FACT_PRE,c.mon, c.F_CHARGE_MODE,IFNULL(n.NOTE_PRINT_NO,'')NOTE_PRINT_NO,n.PRICE_TYPE_ID,c.ARREARS, w.start_num,w.end_num,w.factor_num from CHARGE_INFO c left join ARREARAGE_INFO a ON a.ARREARAGE_NO=c.ARREARAGE_NO left join NOTE_INFO n ON c.id=n.CHARGE_INFO_ID left join METER_MONEY_INFO m on c.METER_MONEY_ID=m.ID left join WRITE_FILES_INFO w on w.METER_ID=a.METER_ID AND w.MON=a.MON AND w.SN=a.SN AND w.time_seg=0 where c.OPERATOR!=9999 and c.f_CHARGE_MODE not in (4,5,6)
and n.SETTLEMENT_NO =#{settParam}
and c.OPERATOR =#{userParam}
and c.BUSINESS_PLACE_CODE =#{deptParam}
and a.WRITE_SECT_ID =#{writeSectIdParam}
and c.mon>=#{startMon}
and c.mon<=#{endMon}
and c.f_charge_mode=#{fChargeMode}
and c.pay_date >= #{startDate}
and c.pay_date <= #{endDate}
